import React, { useState } from 'react';
import { supabase } from '../../services/supabase';
import './Contacts.css';

const Contacts = () => {
  const [newContact, setNewContact] = useState({
    counterparty_name: '',
    email: '',
    phone: '',
    dtc_number: ''
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);

    try {
      const { data, error: supabaseError } = await supabase
        .from('contacts')
        .insert([newContact])
        .select();

      if (supabaseError) throw supabaseError;

      setSuccess(true);
      setNewContact({
        counterparty_name: '',
        email: '',
        phone: '',
        dtc_number: ''
      });
    } catch (err) {
      console.error('Error adding contact:', err);
      setError(err.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewContact(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="contacts-container">
      <h2>Add New Contact</h2>
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">Contact added successfully!</div>}

      <form onSubmit={handleSubmit} className="contact-form">
        <div className="form-group">
          <label htmlFor="counterparty_name">Counterparty Name*</label>
          <input
            type="text"
            id="counterparty_name"
            name="counterparty_name"
            value={newContact.counterparty_name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email*</label>
          <input
            type="email"
            id="email"
            name="email"
            value={newContact.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={newContact.phone}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="dtc_number">DTC Number</label>
          <input
            type="text"
            id="dtc_number"
            name="dtc_number"
            value={newContact.dtc_number}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="submit-button">
          Add Contact
        </button>
      </form>
    </div>
  );
};

export default Contacts;
