import React, { useState } from 'react';
import { useContacts } from '../../hooks/useContacts';
import './Contacts.css';

const Contacts = () => {
  const { contacts, loading, error, addContact, updateContact, deleteContact } = useContacts();
  const [editingId, setEditingId] = useState(null);
  const [newContact, setNewContact] = useState({
    name: '',
    email: '',
    phone: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await addContact(newContact);
    if (result.success) {
      setNewContact({ name: '', email: '', phone: '' });
    }
  };

  const handleUpdate = async (id, updatedContact) => {
    const result = await updateContact(id, updatedContact);
    if (result.success) {
      setEditingId(null);
    }
  };

  if (loading) return <div>Loading contacts...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="contacts-container">
      <h2>Contacts</h2>

      <form onSubmit={handleSubmit} className="contact-form">
        <input
          type="text"
          placeholder="Name"
          value={newContact.name}
          onChange={(e) => setNewContact({...newContact, name: e.target.value})}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={newContact.email}
          onChange={(e) => setNewContact({...newContact, email: e.target.value})}
          required
        />
        <input
          type="tel"
          placeholder="Phone"
          value={newContact.phone}
          onChange={(e) => setNewContact({...newContact, phone: e.target.value})}
          required
        />
        <button type="submit">Add Contact</button>
      </form>

      <div className="contacts-list">
        {contacts.map(contact => (
          <div key={contact.id} className="contact-card">
            {editingId === contact.id ? (
              <div className="edit-form">
                <input
                  type="text"
                  value={contact.name}
                  onChange={(e) => handleUpdate(contact.id, { ...contact, name: e.target.value })}
                />
                <input
                  type="email"
                  value={contact.email}
                  onChange={(e) => handleUpdate(contact.id, { ...contact, email: e.target.value })}
                />
                <input
                  type="tel"
                  value={contact.phone}
                  onChange={(e) => handleUpdate(contact.id, { ...contact, phone: e.target.value })}
                />
                <button onClick={() => setEditingId(null)}>Save</button>
              </div>
            ) : (
              <>
                <h3>{contact.name}</h3>
                <p>{contact.email}</p>
                <p>{contact.phone}</p>
                <div className="contact-actions">
                  <button onClick={() => setEditingId(contact.id)}>Edit</button>
                  <button onClick={() => deleteContact(contact.id)}>Delete</button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Contacts;
